mapper.select:
SELECT STORE_NO, NAME, CATEGORY, TEL, LOCATION, ADDRESS, BREAK_TIME, BUSINESS_HOURS
FROM (
SELECT STORE_NO, NAME, CATEGORY, TEL, LOCATION, ADDRESS, BREAK_TIME, BUSINESS_HOURS,
ROW_NUMBER() OVER(ORDER	BY STORE_NO	DESC) AS RNUM
FROM STORE
WHERE 1=1

) WHERE RNUM BETWEEN #{rowStart}
AND #{rowEnd}
ORDER BY STORE_NO DESC
